mapper.select:
SELECT s.*, c.name as clazzName FROM student s LEFT JOIN clazz c ON s.clazz_id = c.id


        s.name like concat('%', #{name}, '%')


        AND s.degree = #{degree}


        AND s.clazz_id = #{clazzId}


ORDER BY s.update_time DESC

mapper.update:
UPDATE student
violation_score = violation_score + #{score}, violation_count = violation_count + 1, update_time = now()
WHERE id = #{id}
